class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = int(1e9 + 7)
        n = len(nums)
        m = max(nums) + 1

        dp = [[0 for _ in range(m + 1)] for _ in range(n + 1)]
        p1 = [0 for _ in range(m + 2)]
        p2 = [0 for _ in range(m + 2)]
        for i in range(n):
            # print(dp, p1, p2)
            for j in range(m + 1):
                if i == 0:
                    if nums[i] - j >= 0:
                        dp[i][j] = 1
                    else:
                        dp[i][j] = 0
                else:
                    k = nums[i - 1] - nums[i] + j
                    if nums[i] - j >= 0:
                        if k >= m:
                            k = m
                        if k < 0:
                            dp[i][j] = 0
                            continue
                        if k >= j:
                            k = j
                        dp[i][j] = p1[k]
                    else:
                        dp[i][j] = 0
                    # for k in range(j + 1):
                        # if nums[i - 1] - k >= nums[i] - j and nums[i] - j >= 0:
                            # dp[i][j] = (dp[i][j] + dp[i - 1][k]) % MOD

                if j != 0:
                    p2[j] = (p2[j - 1] + dp[i][j]) % MOD
                else:
                    p2[j] = (0 + dp[i][j]) % MOD

            # print(p1, p2, dp[i])
            p1 = list(p2)
            p2 = [0 for _ in range(m + 2)]



        return sum(dp[n - 1]) % MOD
