#define ll long long
const ll mod = 1e9 + 7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<ll> dp(1001);
        for (int i = 0; i <= 1000; i++) {
            if (i <= nums[0]) dp[i] = 1;
        }
        for (int i = 1; i < nums.size(); i++) {
            vector<ll> upd(1002);
            for (int j = 0; j <= min(nums[i - 1], 1000); j++) {
                int r = min(nums[i], 1000), l = max({nums[i] - nums[i - 1] + j, 0, j});
                if (r >= l) {
                    upd[r + 1] -= dp[j];
                    upd[l] += dp[j];
                }
            }
            ll acc = 0;
            for (int j = 0; j <= 1000; j++) {
                acc = ((acc + upd[j]) % mod + mod) % mod;
                dp[j] = acc;
            }
        }
        ll res = 0;
        for (int i = 0; i <= 1000; i++) res = (res + dp[i]) % mod;
        return res;
    }
};
