const int MOD = 1000000007;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(1001, 0));
        for(int j = 0; j <= nums[0]; j++) dp[0][j] = 1;
        for(int i = 1; i < n; i++) {
            int acc = 0;
            int offset = max(0, nums[i] - nums[i - 1]);
            for(int j = offset; j <= nums[i]; j++) {
                acc = (acc + dp[i - 1][j - offset]) % MOD;
                dp[i][j] = acc;
            }
        }
        int ans = 0;
        for(int j = 0; j <= nums[n - 1]; j++) ans = (ans + dp[n - 1][j]) % MOD;
        return ans;
    }
};
