class Solution {
public:
typedef long long ll;
const ll mod = 1e9+7;
    int countOfPairs(vector<int>& a) {
        vector<vector<ll>>dp(a.size()+1,vector<ll>(1001));
        for(int i=0;i<=a[0];i++){
            dp[0][i]=1;
        }
        for(int i=1;i<dp[0].size();i++){
            dp[0][i]+=dp[0][i-1];
            dp[0][i]%=mod;
        }
        for(int i=1;i<a.size();i++){
            for(int j=0;j<=a[i];j++){
                if(j-max(0,a[i]-a[i-1])>=0)
                    dp[i][j]=dp[i-1][j-max(0,a[i]-a[i-1])];
                dp[i][j]%=mod;
            }
            for(int j=1;j<dp[i].size();j++){
                dp[i][j]+=dp[i][j-1];
                dp[i][j]%=mod;
            }
        }
        // for(int i=0;i<dp.size();i++){
        //     for(int j=0;j<=3;j++){
        //         cout<<dp[i][j]<<" ";
        //     }
        //     cout<<endl;
        // }
        return dp[a.size()-1][1000];

    }
};
