class Solution {
    final long mod = 1_000_000_007L;
    public int countOfPairs(int[] nums) {
        long ans = 0;
        long[][] dp = new long[nums.length+1][1001];
        Arrays.fill(dp[nums.length],1);
        long[] prefix = new long[1001];
        prefix[0] = 1;
        for(int i=1;i<1001;i++) prefix[i]=prefix[i-1]+1;
        for(int i=nums.length-1;i>0;i--){
            for(int j=0;j<=1000;j++){
                int l = Math.max(j,nums[i]-nums[i-1]+j);
                int r = nums[i];
                if(l>r) continue;
                dp[i][j] = prefix[r];
                if(l-1>=0) dp[i][j] -= prefix[l-1];
            }
            prefix[0] = dp[i][0];
            for(int j=1;j<=1000;j++) prefix[j] = (prefix[j-1]+dp[i][j]+mod)%mod;
        }
        return (int)prefix[nums[0]];
    }
}
