class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1000000007;
        vector< vector<int> > dp;
        for (int i = 0; i < nums.size(); i++) {
            vector<int> row(1001);
            if (i == 0) {
                for (int j = 0; j <= 1000; j++) row[j] = ((j <= nums[i]) ? 1 : 0);
            } else {
                long long count = 0;
                for (int j = 0; j <= 1000; j++) {
                    if (j > nums[i]) row[j] = 0;
                    else {
                        if ((j - nums[i] + nums[i - 1]) >= 0) count = (count + (long long)(dp[i - 1][min(j, j - nums[i] + nums[i - 1])])) % MOD;
                        row[j] = (int)(count);
                    }
                }
            }
            dp.push_back(row);
        }
        long long result = 0;
        for (int i = 0; i <= 1000; i++) result = (result + dp[nums.size() - 1][i]) % MOD;
        return (int)(result);
    }
};
