#define sz(x) int(x.size())
#define all(x) x.begin(), x.end()
#define db double
#define ll long long
#define ar array
template<typename T> bool ckmin(T& a, const T& b) { return a > b ? a = b, 1 : 0; }
template<typename T> bool ckmax(T& a, const T& b) { return a < b ? a = b, 1 : 0; }

class Solution {
public:
    const int N = 50, mod = 1e9+7;
    void fadd(int &a, int b) {
        a += b;
        if (a >= mod) a -= mod;
    }
    int countOfPairs(vector<int>& nums) {
        vector<int> dp;
        for (int i = 0; i < sz(nums); i++) {
            if (!i) {
                dp.resize(nums[i] + 1, 1);
            }
            else {
                vector<int> dp2(nums[i] + 1);
                for (int j = 0; j <= nums[i]; j++) {
                    int u = max(0, nums[i] - nums[i-1]);
                    if (j - u >= 0) fadd(dp2[j], dp[j - u]);
                    // for (int k = 0; k < sz(dp); k++) {
                    //     if (j - u >= k) {
                    //         fadd(dp2[j], dp[k]);
                    //     }
                    // }
                }
                swap(dp, dp2);
            }
            for (int i = 1; i < sz(dp); i++) fadd(dp[i], dp[i-1]);
        }
        return dp.back();
    }
};
