#include <vector>
using namespace std;
class Solution {
public:
#define ll long long
    int countOfPairs(vector<int>& nums) {
        ll MOD=1e9+7;
        int n=nums.size();
        vector<vector<ll>> dp(n,vector<ll>(1001,0));
        for(int i=0;i<=nums[0];i++) {
            dp[0][i]=1;
        }
        for(int i=1;i<n;i++) {
            vector<ll>p(nums[i]+1,0);
            p[0]=dp[i-1][0];
            for(int j=1;j<p.size();j++){p[j]=p[j-1]+dp[i-1][j];p[j]%=MOD;}

            for(int j=0;j<=nums[i];j++) {
                int mx=min(j,nums[i-1]-nums[i]+j);
                if(mx<0)continue;

                dp[i][j]+=(p[mx])%MOD;dp[i][j]%=MOD;

            }
        }
        ll result=0;
        for(int i=0;i<=nums[n-1];i++) {
            result=(result+dp[n-1][i])%MOD;
        }
        return result%MOD;
    }
};
