class Solution {
public:
    int countOfPairs (vector<int> &A) {
        long long MOD = 1e9 + 7; int N = size(A);
        vector<vector<long long>> DP(N, vector<long long> (1001));

        fill(begin(DP[0]), begin(DP[0]) + A[0] + 1, 1);
        for (int j = 1; j <= 1000; j++) DP[0][j] += DP[0][j - 1];

        for (int i = 1; i < N; i++) {
            for (int j = 0; j <= 1000; j++) {
                if (j) DP[i][j] = DP[i][j - 1];
                if (j > A[i]) continue;

                int k = A[i - 1] - A[i] + j;
                if (k < 0) continue; k = min(k, j);

                DP[i][j] += DP[i - 1][k];
                DP[i][j] %= MOD;
            }
        }

        return DP[N - 1][1000];
    }
};
