class Solution {
public:
    int mod=1e9+7;
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int>>dp(n, vector<int>(1001));
        for(int i=0;i<=nums[0];i++){
            dp[0][i]=1;
        }

        for(int i=1;i<n;i++){
            int diff=max(0, nums[i]-nums[i-1]);
            vector<int>pre(1002);
            for(int j=1;j<=1001;j++){
                pre[j]=pre[j-1]+dp[i-1][j-1];pre[j]%=mod;
            }
            for(int k=max(0, diff);k<=nums[i];k++){
                // j<=k-diff
                // j=0~k
                // int r=nums[i]-k;// prev=nums[i-1]-j
                dp[i][k]+=pre[max(0, k-diff+1)];
                dp[i][k]%=mod;

            }
//             for(int j=0;j<=nums[i-1];j++){
//                 for(int k=j;k<=nums[i];k++){
//                     if(nums[i]-k<=nums[i-1]-j){
//                         dp[i][k]+=dp[i-1][j];
//                         dp[i][k]%=mod;
//                     }               
//                 }
//             }
        }

        int ans=0;
        for(int i=0;i<=1000;i++){
            ans+=dp[n-1][i];
            ans%=mod;
        }
        return ans;
    }
};
