class Solution {
public:
    typedef long long ll;
    ll mod=1e9+7;
    ll dp[2][1055]={0};
    int countOfPairs(vector<int>& numss) {
        vector<ll> a;
        for(int i=0;i<numss.size();i++){
            a.push_back(numss[i]);
        }
        ll n=a.size();
        for(ll j=0;j<=a[0];j++){
            dp[0][j]=1;
        }
        ll cur=0,nxt=1;
        for(ll i=1;i<a.size();i++){
            for(ll j=0;j<=a[i];j++){
                ll k=min(a[i-1]-a[i]+j,j);
                if(k>=0) dp[nxt][j]=dp[cur][k];
            }
            for(int j=0;j<=1000;j++){
                dp[cur][j]=0;
            }
            for(int j=1;j<=1000;j++){
                dp[nxt][j]=(dp[nxt][j]+dp[nxt][j-1])%mod;
            }
            swap(cur,nxt);
        }
        ll ans=0;
        for(ll i=0;i<=a[n-1];i++){
            ans+=dp[cur][i];
        }
        return ans%mod;
    }
};
