#define mod 1000000007

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int MAX = 1001;
        int n = nums.size();
        vector<vector<int> > dp(n+1, vector<int> (MAX+1, 0));
        for(int i = 0; i<MAX; i++) {
            if(i > 0)
                dp[1][i] = dp[1][i-1];
            if(i <= nums[0])
                dp[1][i] += 1;
        }

        for(int i = 1; i<n; i++) {
            for(int j = 0; j<=MAX; j++) {
                if(j > 0)
                    dp[i+1][j] = dp[i+1][j-1];
                int currentArr2 = nums[i] - j;
                int preArr1 = min(j, nums[i-1] - currentArr2);
                if(preArr1 < 0 || currentArr2 < 0) continue;
                dp[i+1][j] = (dp[i+1][j] + dp[i][preArr1])%mod;
                // for(int k = 0; k<=preArr1; k++) {
                //     dp[i+1][j] = (dp[i+1][j] + dp[i][k])%mod;
                // }
            }
        }
        // for(int i = 0; i<=n; i++) {
        //     for(int j = 0; j<MAX; j++) cout<<dp[i][j]<<" ";
        //     cout<<endl;
        // }
        // int ans = 0;
        // for(int i = 0; i<MAX; i++) ans = (ans + dp[n][i])%mod;
        return dp[n][MAX-1];
    }
};
