class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        long long i, j, n = nums.size(), ans = 0, dp[n][2001], dp2[n][2001], x, y, mod = 1e9 + 7, e1, e2, en;
        for(i=0;i<n;i++)    for(j=0;j<2001;j++)   dp[i][j] = 0, dp2[i][j] = 0;
        for(j=0;j<=nums[0];j++){
            if(j)   dp2[0][j] = dp2[0][j-1];
            dp[0][j] = 1;
            dp2[0][j] += dp[0][j];
        }

        for(i=1;i<n;i++){
            for(j=0;j<=nums[i];j++){
                if(j)   dp2[i][j] = dp2[i][j-1];
                e1 = j;
                e2 = nums[i-1] - (nums[i] - j);
                en = min(e1, e2);

                if(en >= 0){
                    dp[i][j] = dp2[i-1][en];
                    dp2[i][j] = (dp2[i][j] + dp[i][j]) % mod;
                }
            }
        }

        for(j=0;j<=nums[n-1];j++) ans = (ans + dp[n-1][j]) % mod;

        return ans;
    }
};
