class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        prevsum = list(range(nums[-1] + 1, 0, -1))

        for i in range(len(nums) - 2, -1, -1):
            currsum = [0] * (nums[i] + 2)
            for j in range(nums[i], -1, -1):
                currsum[j] = currsum[j + 1]
                previ = max(j, nums[i + 1] - (nums[i] - j))
                if previ >= len(prevsum):
                    continue
                currsum[j] += prevsum[previ]
            prevsum = currsum

        return prevsum[0] % 1000000007
