MOD = 10**9 + 7

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        up = max(nums) + 1
        dp = [[0] * up for _ in range(n)]
        for i in range(nums[0] + 1):
            dp[0][i] = 1 + i
        for i in range(nums[0] + 1, up):
            dp[0][i] = dp[0][i-1]

        for i, d in enumerate(nums):
            if i == 0: continue
            tmp = [0] * up    
            for cur in range(nums[i] + 1):
                end = min(nums[i-1]- nums[i] + cur, cur)
                if end < 0: continue
                tmp[cur] += dp[i-1][end]
                tmp[cur] %= MOD
            dp[i] = list(accumulate(tmp))    
        # print(*dp)        
        return dp[n-1][up-1]% MOD
