class Solution {
public:
    using LL = long long;
    LL M = 1e9+7;
    LL dp[2001][1001];
    LL preSum[1001];
    int countOfPairs(vector<int>& nums) {
        memset(dp, 0, sizeof(dp));
        memset(preSum, 0, sizeof(preSum));
        int n = nums.size();
        for (int j=0; j<=nums[0]; j++) {
            dp[0][j] = 1;
        }
        preSum[0] = dp[0][0];
        for (int j=1; j<=1000; j++) {
            preSum[j] = preSum[j-1] + dp[0][j];
            preSum[j] %= M;
        }
        for (int i=1; i<n; i++) {
            for (int j=0; j<=nums[i]; j++) {
                dp[i][j] += preSum[min(j, nums[i-1]-nums[i]+j)];
                dp[i][j] %= M;
            }
            preSum[0] = dp[i][0];
            for (int j=1; j<=nums[i]; j++) {
                preSum[j] = preSum[j-1] + dp[i][j];
                preSum[j] %= M;
            }
        }
        LL ans = 0;
        for (int j=0; j<=1000; j++) {
            ans += dp[n-1][j];
            ans %= M;
        }
        return ans;
    }
};
