#define ll long long 
#define fr(i,s,e) for(ll i=s;i<e;i++)
const ll mod=1e9+7;
ll dp[2010][1005];
ll pre[2010][1005];
ll pref(ll lvl,ll last,vector<int>&nums);
ll rec(ll lvl,ll last,vector<int>&nums){
    if(lvl==nums.size())return 1;
    if(dp[lvl][last]!=-1)return dp[lvl][last];
    ll mx=max(nums[lvl]+last-nums[lvl-1],last);
    ll ans=pref(lvl,mx,nums);
    return dp[lvl][last]=ans;
}
ll pref(ll lvl,ll last,vector<int>&nums){
    if(lvl==nums.size())return 1;
    if(last>nums[lvl])return 0;
    if(pre[lvl][last]!=-1)return pre[lvl][last];
    ll ans=rec(lvl+1,last,nums);
    (ans+=pref(lvl,last+1,nums))%=mod;
    return pre[lvl][last]=ans;
}
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        ll n=nums.size();
        ll mx=*max_element(nums.begin(),nums.end());
        fr(i,0,n+1)fr(j,0,mx+1)dp[i][j]=pre[i][j]=-1;
        ll ans=0;
        // fr(i,0,nums.back()+1)dp[n][i]=1;
        // fr(i,0,nums.back()+1)
        // for(ll i=n-1;i>=0;i--){
        //     fr(last,0,mx+1){
        //         if()
        //     }

        // }
        fr(i,0,nums[0]+1){
            (ans+=rec(1,i,nums))%=mod;
        }
        return ans;
    }
};
