class Solution {
public:
static const int MODULO = 1e9 + 7;
    vector<vector<int>> dp;
    vector<int> num;

    int rec(int ind, int sum) {

        if(sum>num[ind]){
            return 0;
        }
        if (ind == num.size()-1) {
            return num[ind]-sum+1; 
        }

        if (dp[ind][sum] != -1) {
            return dp[ind][sum]; 
        }

        int ans = 0;
        ans= (0LL+ans+rec(ind,sum+1))%MODULO;
        int k= num[ind+1]-num[ind]+sum;
        k = max(k,sum);
        ans= (0LL+ans+ rec(ind+1,k))%MODULO;


        dp[ind][sum] = ans;
        return ans;
    }
    int countOfPairs(vector<int>& nums) {
        num = nums;
        int n = nums.size();
        dp.assign(n, vector<int>(2001, -1));

        return rec(0, 0);
    }
};
