const M: i32 = 1000000007;

impl Solution {
    pub fn count_of_pairs(nums: Vec<i32>) -> i32 {
        let n = nums.len();
        let mut dp = vec![vec![0; 1001]; n];
        for j in 0..=1000 as usize {
            if j <= nums[0] as usize {
                dp[0][j] += 1;
            }
            if j > 0 {
                dp[0][j] += dp[0][j - 1];
            }
        }
        for i in 1..n {
            for j in 0..=1000 as usize {
                if j <= nums[i] as usize {
                    if nums[i - 1] < nums[i] {
                        if j >= (nums[i] - nums[i - 1]) as usize {
                            dp[i][j] = (dp[i][j] + dp[i - 1][j - (nums[i] - nums[i - 1]) as usize]) % M;
                        }
                    } else {
                        dp[i][j] = (dp[i][j] + dp[i - 1][j]) % M;
                    }
                    // if j + nums[i - 1] as usize > nums[i] as usize {
                    //     dp[i][j] = (dp[i][j] - dp[i - 1][j + nums[i - 1] as usize - nums[i] as usize - 1] + M) % M;
                    // }
                }
                if j > 0 {
                    dp[i][j] = (dp[i][j] + dp[i][j - 1]) % M;
                }
                // if j <= nums[i] as usize {
                //     println!("dp[{}][{}]={}", i, j, dp[i][j]);
                // }
            }
        }

        dp[n - 1][1000]
    }
}
