import math

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        count = 0
        n = len(nums)
        for i in range(1, n):
            count += max(0, nums[i] - nums[i - 1])
        if nums[-1] < count:
            return 0
        remain = nums[-1] - count
        return math.factorial(remain + n) // math.factorial(n) // math.factorial(remain) % 1000000007
