class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n=len(nums)
        l=[0]*1001
        for i in range(nums[0]+1):
            l[i]=1
        for i in range(1,n):
            temp=[0]*1001
            for j in range(1001):
                if l[j]!=0:
                    inc,dec=j,nums[i-1]-j
                    floor=max(j,nums[i]-nums[i-1]+j)
                    if floor>nums[i]:
                        break
                    else:
                        temp[floor]+=l[j]
                        if nums[i]<1000:
                            temp[nums[i]+1]-=l[j]
            for i in range(1,1001):
                temp[i]+=temp[i-1]
            l=temp
        return sum(l)%(10**9+7)
