class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = int(1e9) + 7
        n = len(nums)
        m = max(nums) + 1

        dp = [0] * m
        next_dp = [0] * m

        for i in range(m):
            dp[i] = 1

        for i in range(1, n):
            next_dp = [0] * m
            psum = [0] + list(accumulate(dp))
            for small in range(nums[i] + 1):
                big = nums[i] - small

                if nums[i - 1] - big + 1 <= 0:
                    continue
                next_dp[small] = (psum[min(small + 1, nums[i - 1] - big + 1)]) % MOD
            dp = next_dp[:]
        return sum(dp) % MOD
