const int mod = 1e9 + 7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size(), m = *max_element(nums.begin(), nums.end());
        vector<int> dp(m + 2, 1);
        for (int i = m; i >= 0; i--) {
            dp[i] += dp[i + 1];
        }

        for (int i = n - 1; i >= 1; i--) {
            vector<int> curr(m + 2, 0);
            for (int j = m; j >= 0; j--) {
                int k = nums[i - 1] - j;
                if (k < 0) {
                    continue;
                } else {
                    int l = max(j, nums[i] - k), r = nums[i];
                    if (l > r) continue;
                    curr[j] = (dp[l] - dp[r + 1] + mod) % mod;
                }
            }

            for (int j = m; j >= 0; j--) {
                curr[j] += curr[j + 1];
                curr[j] %= mod;
            }
            dp = curr;
        }

        return (dp[0] - dp[nums[0] + 1] + mod) % mod;
    }
};
