class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        const int mod = 1e9 + 7;
        const int maxValue = 1000;  

        vector<vector<long long>> dp(2, vector<long long>(maxValue + 1, 0));
        vector<long long> prefix(maxValue + 2, 0);

        for (int j = 0; j <= nums[0]; j++) {
            dp[0][j] = 1;
            prefix[j + 1] = (prefix[j] + 1) % mod;
        }

        int curr = 0, prev = 0;
        for (int i = 1; i < n; i++) {
            curr = i % 2;
            prev = 1 - curr;
            vector<long long> prefix2(maxValue + 2, 0);

            for (int j = 0; j <= nums[i]; j++) {
                int previousMax = min(j, nums[i-1] - (nums[i] - j));
                dp[curr][j] = (previousMax >= 0) ? prefix[previousMax + 1] : 0;
                prefix2[j + 1] = (prefix2[j] + dp[curr][j]) % mod;
            }

            prefix = move(prefix2);
        }

        long long ans = 0;
        for (int j = 0; j <= nums[n-1]; j++) {
            ans = (ans + dp[curr][j]) % mod;
        }
        return ans;
    }
};
