class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int mod=1e9+7;
        int n=nums.size();
        vector<int>prev(1001,1);
        vector<int>curr(1001,0);
        vector<int>prevsuffixsum(1002,1);
        vector<int>currsuffixsum(1002,0);
        for(int j=1000;j>=0;j--){
            prevsuffixsum[j]+=prevsuffixsum[j+1];
        }
        for(int i=n-1;i>=0;i--){
            for(int j=1000;j>=0;j--){
                int sl=(i==0)?1e9:nums[i-1]-j;
                int l=max(j,nums[i]-sl);
                int u=nums[i];
                if(u<l)curr[j]=0;
                else{
                    curr[j]=(prevsuffixsum[l]-prevsuffixsum[u+1]+mod)%mod;
                }
                currsuffixsum[j]=(curr[j]+currsuffixsum[j+1])%mod;
            }
            prevsuffixsum=currsuffixsum;
            prev=curr;
        }
        return prev[0];
    }
};
