class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int mx=1000;
        vector<long long> dp1(mx+1, 1), dp2(mx+1, 0);
        vector<long long> suff1(mx+2, 1), suff2(mx+2, 0);
        suff1[mx+1]=0;
        int mod=1e9+7;

        for(int i=0; i<n; i++)
        {
            // cout<<i<<endl;
            for(int j=mx; j>=0; j--)
            {
                dp2[j]=0;
                if(j<=nums[i])
                {
                    int x=j;
                    if(i==0 || nums[i-1]-j<=nums[i]-j) x=j;
                    else
                    {
                        x=j+nums[i-1]-nums[i];
                    }
                    dp2[j]=suff1[x];
                }
                suff2[j]=suff2[j+1]+dp2[j];
                suff2[j]%=mod;
            }
            suff1=suff2;
            dp1=dp2;
            // cout<<"i = "<<i<<endl;
            // for(int i=0; i<mx+1; i++) cout<<dp1[i]<<" ";
            // cout<<endl;
            // for(int i=0; i<mx+1; i++) cout<<suff1[i]<<" ";
            // cout<<endl;
        }

        return suff1[0];
    }
};
