class Solution {
    vector<vector<int>> dp;
    int mod = 1e9+7;
    int N = 1001;
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp.assign(n+1, vector<int> (N, 1));

        vector<vector<int>> suffix(n+1, vector<int>(N,0));
        for(int j = N-1; j >= 0; --j) suffix[n][j] += ((j == N-1) ? 0 : suffix[n][j+1]) + dp[n][j];

        for(int i = n-1; i >= 0; --i){
            for(int j = N-1; j >= 0; --j){
                if((i > 0 && j > nums[i-1]) || (i == 0 && j > 0)) continue;

                dp[i][j] = 0;

                int a1 = ((i == 0) ? 0 : (j + max(0,nums[i] - nums[i-1])));    // min value of arr1[i] such that arr1[i] >= arr1[i-1] && arr2[i] <= arr2[i-1]
                if(a1 >= 0 && a1 <= nums[i]){
                    dp[i][j] += (suffix[i+1][a1] - (nums[i]+1 < N ? suffix[i+1][nums[i]+1] : 0) + mod) % mod;
                    dp[i][j] %= mod;
                }

                suffix[i][j] += ((j == N-1) ? 0 : suffix[i][j+1]) + dp[i][j];
                suffix[i][j] %= mod;
            }
        }


        return dp[0][0];
    }
};
