#define lli long long
lli mod=1e9+7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<vector<lli>>dp(2002,vector<lli>(1001));
        vector<lli>seg(1001);
        lli n=nums.size();
        for(lli i=n;i>=0;i--){
            //cout<<i<<endl;
            for(lli j=1000;j>=0;j--){
                if(i==n){
                    if(j<=nums[n-1]){
                        dp[i][j]=1;
                    }
                    continue;
                }
                lli ans=0;
                if(i==0){
                    ans+=seg[j];
                }
                else{
                    lli t=max(j,j+nums[i]-nums[i-1]);
                    if(t<=nums[i]){
                        ans+=seg[t];
                    }
                }
                ans=ans%mod;
                //cout<<i<<" "<<j<<" "<<ans<<endl;
                dp[i][j]=ans;
            }
            lli ans=0;
            for(lli j=1000;j>=0;j--){
                //cout<<j<<endl;
                ans+=dp[i][j];
                ans=ans%mod;
                seg[j]=ans;
            }
        }
        return (int)(dp[0][0]%mod);
    }
};
