#define ll long long
#define sz(x) (int)x.size()
#define all(x) (x).begin(), (x).end()
#define MAX(x) *max_element(all(x))
#define SUM(x) accumulate(all(x), 0)
#define vll vector<ll>
#define vvll vector<vector<ll>>
#define MOD 1000000007

template<class T> void pv(vector<T> &v){cout<<'{';for(auto&x:v)cout<<x<<',';cout<<"\b}"<<endl;}
template<class T> void pvv(vector<vector<T>> &v){cout << "---\n";for(auto&x:v){pv(x);}cout << "---"<<endl;}
template<class T> void pvp(vector<T> &v){cout<<'{';for(auto&x:v)cout<<'('<<x.F<<','<<x.S<<')';cout<<"}"<<endl;}

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size(), mx = MAX(nums);
        vvll dp(n, vll(mx+1));
        for(int i = 0; i <= nums[0]; ++i) dp[0][i]++;
        for(int i = 1; i < n; ++i){
            vll pref(mx+1);
            pref[0] = dp[i-1][0];
            for(int j = 1; j <= mx; ++j)
                pref[j] = (dp[i-1][j] + pref[j-1]) % MOD;
            for(ll j = 0; j <= nums[i]; ++j){
                ll cur = nums[i] - j;
                ll tmp = nums[i-1] - cur;
                if(tmp>=0)
                    dp[i][j] = pref[min(j, tmp)];
            }
        }
        ll tot = 0;
        for(int i = 0; i <= nums.back(); ++i) tot = (tot + dp.back()[i]) % MOD;
        return tot;
        //return SUM(dp.back())%MOD;
    }
};
