func countOfPairs(nums []int) int {
    maxNum := slices.Max(nums)
    counts := make([][]int, maxNum + 1)
    for n := 0; n <= maxNum; n++ {
        counts[n] = make([]int, len(nums))
        counts[n][0] = n + 1
    }
    m := 1000000007
    for i := 1; i < len(nums); i++ {
        start := 0
        if nums[i] > nums[i - 1] {
            start = nums[i] - nums[i - 1]
        }
        for x := start; x <= nums[i]; x++ {
            y := nums[i] - x
            j := min(x, nums[i - 1] - y)
            if j >= 0 {
                sum := (counts[x][i] + counts[j][i - 1] % m) % m
                if x == 0 {
                    counts[x][i] = sum
                } else {
                    counts[x][i] = (sum + counts[x - 1][i]) % m
                }
            }
        }
    }
    return counts[nums[len(nums) - 1]][len(nums) - 1]
}
