class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        if(n == 1) {
            return nums[0] + 1;
        }
        int maxi = *max_element(nums.begin(), nums.end());
        long long ans = 0;
        long long MOD = 1e9 + 7;
        vector<vector<long long>> dp(n + 1, vector<long long>(maxi + 1));
        dp[n][maxi] = 1;
        for(int j = maxi - 1; j >= 0; j--) {
            dp[n][j] = 1 + dp[n][j + 1];
            dp[n][j] %= MOD;
        }

        for(int i = n - 1; i >= 0; i--) {
            for(int j = nums[i]; j >= 0; j--) {
                if(i == n - 1) {
                    dp[i][j] = 1;
                    if(j != nums[i]) dp[i][j] += dp[i][j + 1];
                    continue;
                }
                dp[i][j] = dp[i + 1][max(nums[i + 1] - nums[i] + j, j)];

                if(j != nums[i]) dp[i][j] += dp[i][j + 1];
                dp[i][j] %= MOD;
                if(i == 0 && j == 0) {
                    ans = dp[i][j];
                }

            }
        }

        return ans;

    }
};
