class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10 ** 9 + 7
        mx = max(nums)
        dp = [0] * (mx + 1)
        for i in range(nums[0] + 1):
            dp[i] += 1

        for i in range(1, len(nums)):
            num = nums[i]
            prevNum = nums[i - 1]

            prefix = idx = 0
            q = []
            for x1 in range(mx + 1):
                x2 = num - x1 # x2 遞減 

                prev1 = x1
                prev2 = prevNum - prev1 # prev2 遞減
                if prev2 >= 0:
                    q.append((prev2, dp[prev1]))

                if idx < len(q) and q[idx][0] >= x2:
                    prefix = (prefix + q[idx][1]) % MOD
                    idx += 1

                dp[x1] = prefix if x2 >= 0 else 0      


        return sum(dp) % MOD
