import java.util.*;

class Solution {
    static final int M = (int) (1e9 + 7);

    public int countOfPairs(int[] nums) {
        int dp[] = new int[1001];
        for (int i = 0; i <= nums[0]; i++)
            dp[i] = 1;
        for (int i = 1; i < nums.length; i++) {
            int presum = 0;
            int cur[] = new int[1001];
            int pre = 0;
            for (int j = 0; j <= nums[i]; j++) {
                // pre            <=   j
                // nums[i-1]-pre  >=  nums[i]-j
                //           j >= nums[i]-nums[i-1]+pre
                // k = nums[i]-j;
//                if (j>=nums[i]-nums[i-1]+pre)
//
//                presum = presum + dp[j];
//                cur[j] = presum;
                if (j>=nums[i]-nums[i-1]+pre){
                    presum = (presum + dp[pre++])%M;
                }
                cur[j] = presum;

            }
            dp = cur;
        }
        int ans = 0;
        for (int i : dp)
            ans = (ans + i) % M;
        return ans;
    }


    static void tr(Object... o) {
        System.out.println(Arrays.deepToString(o));
    }

}
