class Solution 
{
    public:
    int countOfPairs(vector<int>& nums) 
    {
        const int MAX_N = 2005, MOD = 1e9 + 7;
        /*vector <vector <int> > monotonic_non_increasing(nums.size() + 1, vector <int> (MAX_N));
        vector <vector <int> > sum_non_increasing(nums.size() + 1, vector <int> (MAX_N));
        for(int i = nums.size() - 1; i >= 0; i--)
        {
            for(int n = MAX_N - 1; n >= 0; n--)
            {
                if(n > nums[i])
                {
                    continue;
                }

                if(i == nums.size() - 1)
                {
                    monotonic_non_increasing[i][n] = 1;
                }
                else 
                {
                    monotonic_non_increasing[i][n] = sum_non_increasing[i + 1][n];
                    monotonic_non_increasing[i][n] %= MOD;
                }

                sum_non_increasing[i][n] = sum_non_increasing[i][n + 1] + monotonic_non_increasing[i][n];
                sum_non_increasing[i][n] %= MOD;
            }
        }*/

        vector <vector <int> > no_of_sequences(nums.size() + 1, vector <int> (MAX_N + 2));
        vector <vector <int> > sum_no_sequences(nums.size() + 1, vector <int> (MAX_N + 2));
        for(int i = 0; i < nums.size(); i++)
        {
            for(int n = 0; n < MAX_N; n++)
            {
                if(i == 0)
                {
                    no_of_sequences[i][n] = (n <= nums[i]);
                }
                else 
                {
                    int bound_1 = n, bound_2 = nums[i - 1] - nums[i] + n;
                    int max_previous_n = min(bound_1, bound_2);
                    /*cout << "i = " << i << " n= " << n 
                        << " B1 = " << n << " B2 = " << bound_2 << "\n";*/

                    if(max_previous_n >= 0)
                    {
                        no_of_sequences[i][n] = sum_no_sequences[i - 1][max_previous_n];
                        /*cout << 
                        " Sum (" << i - 1 << "," << max_previous_n << ") = " << 
                        sum_no_sequences[i - 1][max_previous_n] << "\n";*/
                    }
                }

                //cout << "F(" << i  <<"," << n << ") = " << no_of_sequences[i][n] <<" ";

                sum_no_sequences[i][n] = (n > 0 ? sum_no_sequences[i][n - 1] : 0) + no_of_sequences[i][n];
                sum_no_sequences[i][n] %= MOD;

                //cout << "S = " << sum_no_sequences[i][n] << "\n";
            }
        }

        long long answer = sum_no_sequences[nums.size() - 1][nums.back()]; 
        return answer;
    }
};
