class Solution {
public:
int countOfPairs(vector<int>& nums) {
    int modulo = 1e9 + 7;
    int len = nums.size();
    vector<int> mins(len);
    vector<int> maxes(len);
    int min = INT_MAX;
    int max = 0;

    for (int i = len - 1; i >= 0; i--) {
        int n = nums[i];
        min = std::min(min, n);
        max = std::max(max, n);

        mins[i] = min;
        maxes[i] = max;
    }

    int range = max - min;
    int top = nums[0];
    vector<vector<int>> dp(len, vector<int>(top + 1));

    for (int i = 0; i <= top; i++) {
        dp[0][i] = 1;
    }

    int prev = top;

    for (int i = 1; i < len; i++) {
        int min = mins[i];
        int max = maxes[i];
        int range = max - min;
        int n = nums[i];

        int diff = prev - n;
        int total = 0;
        int offset = 0;

        if (diff > 0) {
            top -= diff;
            offset = diff;
        }

        for (int j = top; j >= 0; j--) {
            total += dp[i - 1][j + offset];
            total %= modulo;
            dp[i][j] = total;
        }

        prev = n;
    }

    int result = 0;

    for (int i = 0; i < top + 1; i++) {
        result += dp[len - 1][i];
        result %= modulo;
    }

    return result;
}
};
