class Solution {
    int MOD = (int) 1e9 + 7;
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        long[][] f = new long[n][1001];
        for (int a = 0; a <= nums[0]; a++) {
            f[0][a] = 1;
        }
        for (int i = 1; i < f.length; i++) {
            int num = nums[i];
            long prefix = 0;
            int preA = 0;
            for (int a = 0; a <= num; a++) {
                int b = num - a;
                while (nums[i - 1] - preA >= b && preA <= a) {
                    prefix = (prefix + f[i - 1][preA]) % MOD;
                    preA++;
                }
                f[i][a] = prefix;
            }
        }
        long res = 0l;
        for (int i = 0; i <= 1000; i++) {
            res = (res + f[f.length - 1][i]) % MOD;
        }
        return (int) res;
    }
}
