class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int const M = 1e9 + 7;
        vector<vector<int>> dp(n, vector<int>(1001));
        vector<vector<int>> pref(n, vector<int>(1001));
        pref[0][0] = 1;
        for(int x = 1; x <= nums[0]; x++) {
            pref[0][x] = pref[0][x-1]+1;
        }

        for(int i = 1; i < n; i++) {
            for(int x = 0; x <= nums[i]; x++) {
                if(x >= nums[i] - nums[i - 1]) {
                    if(x == 0) pref[i][x] = pref[i-1][0];
                    else pref[i][x] = pref[i][x-1] + (pref[i-1][x - max(0, nums[i] - nums[i - 1])]);
                    pref[i][x] %= M;
                } else if(x > 0) {
                    pref[i][x] = pref[i][x-1];
                }
            }
        }
        return pref[n-1][nums[n-1]];
    }
};
