class Solution {
public:
    int mod=1e9+7;
    int countOfPairs(vector<int>& nums) {
        vector<long long> prev(1001,0),pres(1001,0);
        int size=nums.size();
        prev[0]=1;
        for(int i=1;i<=nums[0];i++)prev[i]+=prev[i-1];
        for(int i=1;i<size;i++)
        {
            for(int j=0;j<=nums[i];j++)
            {
               int mi=min(j,j+nums[i-1]-nums[i]);
               if(mi>=0)
               {
                   pres[j]+=prev[mi];
                   pres[j]%=mod;
               }
            }
            for(int j=0;j<1001;j++)
            {
              prev[j]=pres[j];
              if(j>nums[i])prev[j]=0;
              pres[j]=0;
            }
            prev[0]%=mod;
            for(int j=1;j<1001;j++)
            {
                prev[j]+=prev[j-1];
                prev[j]%=mod;
            }
        }
        long long sum=0;
        for(int i=0;i<=nums[size-1];i++)
        {
            sum+=prev[i];
            sum%=mod;
        }
        return (int)sum;
    }
};
