class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        ll dp[n+1][1001];
        for(ll i=0;i<=n;i++){
            for(ll j=0;j<=1000;j++){
                dp[i][j] = 0;
            }
        }
        for(ll i=0;i<=nums[0];i++){
            dp[0][i] = 1;
            if(i)dp[0][i] += dp[0][i-1];
        }
        for(ll i=1;i<=n-1;i++){
            for(ll j=0;j<=nums[i];j++){
                if(nums[i]<nums[i-1]){
                    dp[i][j] += dp[i-1][j];
                    dp[i][j]%=mod;
                }else{
                    ll del = j-abs(nums[i]-nums[i-1]);
                    if(del>=0){
                        dp[i][j] += dp[i-1][del];
                        dp[i][j]%=mod;
                    }
                }
            }
            for(ll j=0;j<=nums[i];j++){
                if(j){
                dp[i][j]+=dp[i][j-1];
                dp[i][j]%= mod;     
                }

            }
        }
        // trace(dp[1][2]);
        int ans = dp[n-1][nums[n-1]];
        return ans;
    }
};
