class Solution {
public:
    const int MOD = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size(), max_num, i;
        max_num = *max_element(nums.begin(), nums.end());
        vector<vector<int>> dp(n, vector<int> (max_num+1, 0));
        for (int arr1_0 = 0; arr1_0 <= nums[0]; ++arr1_0) {
            dp[0][arr1_0] = 1;
        }
        // for (int i=1;i<n;i++) {
        //     for (int arr1_i=0;arr1_i<=nums[i];arr1_i++) {
        //         int tRes=0;
        //         for (int k=arr1_i;k>=0;k--) {
        //             if (nums[i]-arr1_i<=nums[i-1]-k) {
        //                 tRes=(tRes+dp[i-1][k])%MOD;
        //             }
        //         }
        //         dp[i][arr1_i]=tRes;
        //     }
        // }
        for (int i = 1; i < n; ++i) {
            vector<int> prefix_sum(max_num + 2, 0);  // Prefix sum for quick range sum calculation
            for (int j = 0; j <= max_num; ++j) {
                prefix_sum[j + 1] = (prefix_sum[j] + dp[i-1][j]) % MOD;
            }

            for (int arr1_i = 0; arr1_i <= nums[i]; ++arr1_i) {
                int arr2_i = nums[i] - arr1_i;
                // if (arr1_i>)
                if (arr2_i > max_num) continue;
                int k = arr1_i-(nums[i]-nums[i-1]);
                if (k>=0) dp[i][arr1_i] = prefix_sum[min(k, arr1_i) + 1];
            }
        }
        int result = 0;
        for (int j = 0; j <= max_num; ++j) {
            result = (result + dp[n-1][j]) % MOD;
        }

        return result;
    }
};
