class Solution {
public:
    #define ll long long
    int countOfPairs(vector<int>& nums) {
        ll n = nums.size(), ans = 0;
        vector<vector<ll>> dp;
        ll mod = 1e9 + 7;
        vector<ll> tmp(nums[n - 1] + 1, 1);
        dp.push_back(tmp);
        for(ll i = 1; i <= nums[n - 1]; i++) dp[0][i] += dp[0][i - 1];
        for(ll i = n - 2, j = 1; i >= 0; i--, j++) {
            vector<ll> tmp2(nums[i] + 1, 0);
            dp.push_back(tmp2);
            for(ll k = 0; k <= nums[i]; k++) {
                if(k <= nums[i + 1]) {
                    dp[j][k] = dp[j - 1][nums[i + 1]];
                    ll mn = min((ll)nums[i + 1], max(k, nums[i + 1] - (nums[i] - k)));
                    if(mn) dp[j][k] = (dp[j][k] - dp[j - 1][mn - 1] + mod) % mod;
                }
                if(k) dp[j][k] = (dp[j][k] + dp[j][k - 1]) % mod;
            }
        }
        return dp[n - 1].back();
    }
};
