class Solution {
    int MOD=1e9+7;
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        priority_queue<int> pq;
        pq.push(0);
        cout<<"HELP";
        vector<long long> last(1001,1),now(1001,0),pfs(1002,1),csfm(1002,0);
        for(int j=1000;j>=0;j--)pfs[j] = pfs[j] + pfs[j+1];
        for(int i=n-1;i>=0;i--){
            for(int j=1000;j>=0;j--){
                int sl;
                if(i==0)sl = 1e9;
                else sl = nums[i-1]-j;
                int lumsum=max(nums[i]-sl,j),uuama=nums[i];
                if(lumsum>uuama)now[j]=0;
                else now[j]=(MOD+pfs[lumsum]-pfs[uuama+1])%MOD;
                csfm[j]=(now[j]+csfm[j+1])%MOD;
            }
            pfs=csfm, last=now;
        }
        pq.pop();
        return last[0];
    }
};
