MOD = 10**9 + 7
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        for i, n in enumerate(nums):
            e = [0] * (nums[i] + 2)
            for v in range(n + 1):
                if i == 0:
                    e[v] = 1
                else:
                    # implies other element is n - v, implying i - 1 >= n1 - v
                    n1 = nums[i - 1]
                    e[v] = (e[v - 1] + d[max(-1, min(v, n1 - (n - v)))] ) % MOD
            d = e

        return sum(e) % MOD
