class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = int(10**9) + 7
        cap = [nums[-1]]*len(nums)
        for i in range(len(nums)-2, -1, -1):
            cap[i] = min(nums[i], cap[i+1])
        mx = max(nums)
        n = len(nums)
        dp = [[0 for _ in range(mx+1)] for _ in range(n+1)]
        for x in range(cap[n-1]+1):
            dp[-1][x] = 1
        prefix = [0]*(mx+1)
        acc = 0
        for x in range(cap[n-1], -1, -1):
            acc += dp[-1][x]
            prefix[x] = acc
        for i in range(n-1, -1, -1):
            for prev1 in range(nums[i], -1, -1):
                start = prev1
                if i > 0 and nums[i] - nums[i - 1] + prev1 > start:
                    start = nums[i] - nums[i - 1] + prev1
                if start < len(prefix):
                    dp[i][prev1] += prefix[start]
                    dp[i][prev1] %= mod
            prefix = [0]*(mx+1)
            acc = 0
            for x in range(cap[i], -1, -1):
                acc += dp[i][x]
                prefix[x] = acc
        return dp[0][0]
