#define ll long long
const int MOD = 1e9 + 7;
class Solution {
public:
    ll f(int i, int ar1, vector<int>&nums,vector<vector<ll>>&dp){
        if(i==nums.size()){
            return 1;
        }
        if(dp[i][ar1]!=-1){
            return dp[i][ar1];
        }
        ll cur = 0;
        for(int j = 0 ; j <= nums[i] ; j++){
            if(i==0){
                if(j>=ar1 && nums[i]-j<=1001-ar1){
                    cur += f(i+1,j,nums,dp);
                    cur %= MOD;
                }
            }else{
                if(j>=ar1 && nums[i]-j<=nums[i-1]-ar1){
                    cur += f(i+1,j,nums,dp);
                    cur %= MOD; 
                }
            }
        }
        return dp[i][ar1] = cur;
    }
    int countOfPairs(vector<int>& nums) {
        // int n = nums.size();
        // int v = *max_element(nums.begin(),nums.end());
        // vector<vector<ll>>dp(n+1,vector<ll>(v+1,-1));
        // int ans = f(0,0,nums,dp)%MOD;
        int n = nums.size();
        vector<vector<ll>> dp(n, vector<ll>(1001, 0));
        vector<ll> pref(1002, 0);

        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;
        }

        for (int i = 1; i < n; ++i) {
            pref = vector<ll>(1002,0);
            for (int j = 0; j <= 1000; ++j) {
                pref[j + 1] = (pref[j] + dp[i - 1][j]) % MOD;
            }

            for (int j = 0; j <= nums[i]; ++j) {
                int ar2 = nums[i] - j;
                int ar1 = min(j, nums[i-1] - ar2);
                if (ar1 >= 0) {
                    dp[i][j] = pref[ar1 + 1];
                }
            }
        }

        ll ans = 0;
        for (int j = 0; j <= nums[n - 1]; ++j) {
            ans = (ans + dp[n - 1][j]) % MOD;
        }

        return (int)(ans%MOD);
    }
};
