#define ll long long int
class Solution {
public:
    ll M=1e9+7;
    ll find(ll i,ll pr,vector<int>&nums,vector<vector<int>>& dp)
    {
        if(i>=nums.size())
        return 1;
        if(dp[i][pr]!=-1)
        return dp[i][pr];
        int ans=0;
        if(i==0)
        pr=0;
        for(int j=pr;j<=nums[i];j++)
        {
            if(i==0)
            ans=(ans%M + find(i+1,j,nums,dp)%M)%M;
            else
            {
                if(j>=pr && (nums[i]-j)<=(nums[i-1]-pr))
                ans=(ans%M + find(i+1,j,nums,dp)%M)%M;
            }
        }
        return dp[i][pr]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        vector<ll> presum(1001,0);
        for(int i=0;i<=nums[nums.size()-1];i++)
        presum[i]=1;
        for(ll i=nums.size()-2;i>=0;i--)
        {
            vector<ll> sum(1001,0);
            for(ll i=1;i<=1000;i++)
            presum[i]+=presum[i-1];
            for(ll j=0;j<=nums[i];j++)
            {
                ll val=nums[i+1]-j+1;
                ll d=nums[i]-j;
                ll low=max(j,nums[i+1]-d);
                if(val<=0)
                continue;
                sum[j]=presum[nums[i+1]];
                if(low>0)
                sum[j]=(sum[j]%M - presum[low-1]%M +M)%M;
            }
            presum=sum;
        }
        ll ans=0;
        for(auto x:presum)
        ans=(ans%M + x%M)%M;
        return ans;
    }
};
