class Solution {
public:
    int M = 1000;
    int MOD = 1e9 + 7;

    int add(long long int a, long long int b) {
        return (a%MOD + b%MOD)%MOD;
    }

    int sub(long long int a, long long int b) {
        return (a%MOD - b%MOD + MOD)%MOD; 
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();


        vector<vector<int>> dp(n, vector<int>(M+1, 0));
        vector<vector<int>> dp2(n, vector<int>(M+1, 0));

        for(int j=0; j<=nums[n-1]; j++) {
            dp[n-1][j] = 1;
        }

        for(int j=0; j<=M; j++) {
            if(j) {
                dp2[n-1][j] = add(dp2[n-1][j-1], dp[n-1][j]);
            } else {
                dp2[n-1][j] = dp[n-1][j];
            }
        }

        for(int i=n-2; i>=0; i--) {
            // cout<<"Idx "<<i<<endl;
            for(int j=0; j<=nums[i]; j++) {
                // cout<<"\t"<<j<<endl;
                int st = max(j, nums[i+1]-nums[i]+j);
                int end = nums[i+1];

                int cnt = sub(dp2[i+1][end], (st ? dp2[i+1][st-1] : 0));

                // cout<<"\t"<<st<<" "<<end<<" "<<cnt<<endl;

                dp[i][j] = add(dp[i][j], cnt);
            }

            for(int j=0; j<=M; j++) {
                if(j) {
                    dp2[i][j] = add(dp2[i][j-1], dp[i][j]);
                } else {
                    dp2[i][j] = dp[i][j];
                }
            }
        }

        int ans = 0;
        for(int j=0; j<=M; j++) {
            ans = add(ans, dp[0][j]);
        }

        return ans;
    }
};
