#define ll long long
vector<ll> fact(1e4+1);
void precalc() {
    fact[0]=fact[1]=1;
    for (ll i=2; i<1e4+1; i++) {
        fact[i]=(i*fact[i-1])%1000000007;
    }
}
ll exp (ll a, ll b) {
    if(a==0) return b==0;
    ll ans=1;
    while(b) {
        if(b&1) ans=(ans*a)%1000000007;
        b>>=1;
        a=(a*a)%1000000007;
    }
    return ans;
}
inline ll inv(ll n) {return exp(n, 1000000005);}
inline ll fermatnCr(ll n, ll r) { return (((fact[n]*inv(fact[r]))%1000000007)*inv(fact[n-r])%1000000007); }

class Solution {
public:
    int countOfPairs(vector<int>& a) {
        precalc();
        long long m = a.back();
        int n = a.size();
        long long t = 0;
        for(int i=1; i<n; i++) t+=max(0, a[i]-a[i-1]);
        // cout<<m<<' '<<t<<' '<<m-t+n<<' '<<n;
        if(m<t) return 0;
        return fermatnCr(m-t+n, n);
    }
};
