class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int mod = (int)1e9+7;
        int[][] dp = new int[n][1001];
        Arrays.fill(dp[0], 1);
        int[] sum = new int[1001];
        for(int i = 0; i < 1001; i++){
            if(i == 0){
                sum[i] = dp[0][0];
            }else{
                sum[i] = (sum[i - 1] + dp[0][i]) % mod;
            }
        }
        for(int i = 1; i < n; i++){
            int cur = nums[i];
            int pre = nums[i - 1];
            int[] nextsum = new int[1001];
            for(int a = 0; a <= cur; a++){
                int b = cur - a;
                if(pre >= b){
                    dp[i][a] = sum[Math.min(a, pre -b)];
                }
                if(a == 0){
                    nextsum[a] = dp[i][a];
                }else{
                    nextsum[a] = (nextsum[a - 1] + dp[i][a]) % mod;
                }
                // for(int prea = 0; prea <= a && pre - prea >= cur - a; prea++){
                //     dp[i][a] = (dp[i][a] + dp[i - 1][prea]) % mod;
                // }
            }
            sum = nextsum;
        }
        int ans = 0;
        for(int i = 0; i <= nums[n - 1]; i++){
            ans = (ans + dp[n - 1][i]) % mod;
        }
        return ans;
    }
}
