class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        /*

        5 5 5 5
              0 1
              1 1
              2 1
              3 1
              4 1
              5 1



        */
        int md = 1000000007;
        int t = 0;
        for(auto &c:nums) t = max(c,t);
        t++;
        vector<int> d(t,0);
        for(int i=0;i<=nums.back();++i) d[i]=1;
        for(int j=t-2;j>=0;--j) {d[j]+=d[j+1];d[j]%=md;}
        int n = nums.size();
        for(int i=n-2;i>=0;--i){
            vector<int> p(t,0);
            int k = nums[i+1]-nums[i];
            if(k<0) k= 0;
            for(int j=t-1-k;j>=0;--j){
                p[j]=d[j+k];
            }

                // cout << p[t-1] << " ";
            for(int j=t-2;j>=0;--j) {
                // cout << p[j] << " ";
                p[j]+=p[j+1];p[j]%=md;}
            // cout << endl;
            d = p;
        }

            // for(int j=t-2;j>=0;--j) {
            //     // cout << p[j] << " ";
            //     d[j]+=d[j+1];d[j]%=md;}
        return d[0];
    }
};
